sys_script_include.script:
// test

var LlamaUtils = Class.create();
LlamaUtils.prototype = {

	FARM: 'x_snc_llamafarm_farm',
	LLAMA: 'x_snc_llamafarm_llama',
	LLAMA_HEALTH_CHECK: 'x_snc_llamafarm_llama_health_check',
	STALL: 'x_snc_llamafarm_stall',

    initialize: function() {
    },

	removeLlamaFromStall: function(llamaSysId) {
		gs.info(llamaSysId);
		var stall = new GlideRecord(this.STALL);
		stall.addQuery('occupant', llamaSysId);
		stall.query();

		if (!stall.next()) {
			gs.info('llama not in stall');
			return;
		}

		stall.setValue('occupant', '');

		stall.update();
	},

    type: 'LlamaUtils'
};
